import React from "react";
import { Link } from "react-router-dom";
import Blogrightsidebar from "./Blogrightsidebar";
import BlogGridItem from "./BlogGridItem";
import ServiceData from "../Service/ServiceData";
import { convertToSlug } from "../../helpers";

const BlogGrid = ({ postList }) => {
  return (
    <section id="blog" className="blog_area_two sec_pad">
      <div className="container">
        <div className="row">
          <div className="col-lg-8 blog_grid_info">
            <div className="row">
              {postList?.slice(0, 5).map((post) => {
                const { publishDate, image, text, likes, id } = post;
                return (
                  <BlogGridItem
                    id={id}
                    date={publishDate}
                    // month="jan"
                    image={image}
                    Title={text}
                    description="Only a quid bobby brilliant bugger Jeffrey owt to do with me lurgy blimey.!"
                    btn="Read More"
                    comment={likes}
                    slug={convertToSlug(title)}
                  />
                );
              })}
            </div>
            <ul className="list-unstyled page-numbers shop_page_number text-left mt_30">
              <li>
                <span aria-current="page" className="page-numbers current">
                  1
                </span>
              </li>
              <li>
                <a className="page-numbers" href=".#">
                  2
                </a>
              </li>
              <li>
                <a className="next page-numbers" href=".#">
                  <i className="ti-arrow-right"></i>
                </a>
              </li>
            </ul>
          </div>
          <div className="col-lg-4">
            <Blogrightsidebar ServiceData={ServiceData} />
          </div>
        </div>
      </div>
    </section>
  );
};
export default BlogGrid;
