import React, { Component } from "react";
import Reveal from "react-reveal/Reveal";
import { Link } from "react-scroll";
class Featuresitems extends Component {
  render() {
    var {
      button,
      id,
      to,
      rowClass,
      fimage,
      iImg,
      ftitle,
      descriptions,
      aClass,
    } = this.props;
    return (
      <div id={id} className={`agency_featured_item ${rowClass} `}>
        <div className="col-lg-6">
          <Reveal effect="fadeInLeft">
            <div className="agency_featured_img text-right">
              <img src={require("../../brand/" + fimage)} alt="" />
            </div>
          </Reveal>
        </div>
        <div className="col-lg-6">
          <div
            className={`agency_featured_content wow fadeInLeft ${aClass}`}
            data-wow-delay="0.6s"
          >
            <Reveal effect="fadeInRight">
              <div className="dot">
                <span className="dot1"></span>
                <span className="dot2"></span>
              </div>
              <h3>{ftitle}</h3>
              <p>{descriptions}</p>
              <Link
                spy={true}
                smooth={true}
                to={to}
                className="icon mt_30"
                style={{ display: button }}
              >
                <i className="ti-arrow-right"></i>
              </Link>
            </Reveal>
          </div>
        </div>
      </div>
    );
  }
}

export default Featuresitems;
